var pokemonCards=document.querySelector("#cards-container"),stringSearch=document.querySelector("#search-input"),sortButtons=document.querySelector("#sort-input"),globalPokedex=[];function printf(obj="empty: nothing being printed"){console.log(obj)}async function fetchPokemon(){let response=await fetch("assets/jsons/pokedexAll.json"),data;return await response.json()}function cardMaker(pokemon){const cardElements=pokemon.map(({id:id,name:name,img:img,desc:desc,types:types,height:height,weight:weight})=>`\n    <article class="card">\n    <img src="${img}" alt="${name+" official-artwork image"}">\n    <small class="id">ID: ${id}</small>\n    <p class="name">${name[0].toUpperCase()+name.substring(1)}</p>\n    <span class="types">Types: ${types.join(" ").toUpperCase()}</span>\n    <span class="Height">Height: ${10*height+".0 cm"}</span>\n    <span class="Weight">Weight: ${weight+" kg"}</span>\n    <p class="desc">${desc}</p>\n    </article>\n    `);pokemonCards.innerHTML=cardElements.join("")}async function main(){let pokemon=await fetchPokemon();pokemon=Object.values(pokemon),globalPokedex=pokemon,cardMaker(pokemon)}stringSearch.addEventListener("input",event=>{let searchString=event.target.value,filteredPokedex;cardMaker(globalPokedex.filter(pokemon=>(pokeID=pokemon.id.toString(),pokemon.name.includes(searchString)||pokemon.types.includes(searchString)||pokemon.type0.includes(searchString)||pokeID.includes(searchString))))}),sortButtons.addEventListener("change",event=>{const sortBy=event.target.value;switch(sortBy){case"idAsc":globalPokedex.sort((pokemonA,pokemonB)=>pokemonA.id-pokemonB.id);break;case"idDesc":globalPokedex.sort((pokemonA,pokemonB)=>pokemonB.id-pokemonA.id);break;case"nameAsc":globalPokedex.sort((pokemonA,pokemonB)=>pokemonA.name<pokemonB.name?-1:pokemonA.name>pokemonB.name?1:0);break;case"nameDesc":globalPokedex.sort((pokemonA,pokemonB)=>pokemonA.name>pokemonB.name?-1:pokemonA.name<pokemonB.name?1:0);break;case"typeAsc":globalPokedex.sort((pokemonA,pokemonB)=>pokemonA.types<pokemonB.types?-1:pokemonA.types>pokemonB.types?1:0);break;case"typeDesc":globalPokedex.sort((pokemonA,pokemonB)=>pokemonA.types>pokemonB.types?-1:pokemonA.types<pokemonB.types?1:0)}cardMaker(globalPokedex)}),main();